const d=document.querySelector(".clock");function u(){const t=new Date,e=t.getHours(),o=t.getMinutes(),n=t.getSeconds(),s=[{hand:"hour",degrees:c(e,0,12,0,360)},{hand:"minute",degrees:c(o,0,60,0,360)},{hand:"second",degrees:c(n,0,60,0,360)}];d.title=`Son las ${e}:${o}:${n}`,s.forEach(({hand:l,degrees:a})=>{const r=d.querySelector(`.clock__hand--${l}`);r.style.transform=`rotate(${a}deg)`,r.style.transition=a===0?"none":""})}function c(t,e,o,n,s){return n+(s-n)*(t-e)/(o-e)}setInterval(u,1e3);
